namespace Funnlz\Services;

use Pimple\Container;

//use Funnlz\Configs\GlobalConfig;
use Funnlz\Mappers\MediaMapper;
use Funnlz\Entities\Media;

class MediaService{
    private $app;
    private $mapper;

    public function __construct(Container $app)
    {
        $this->app = $app;
        $this->getMapper();
    }

    protected function getMapper(){
        if($this->mapper==NULL){
            $this->mapper = new MediaMapper($this->app['pdo_adapter']);
        }
        return $this->mapper;
    }
    public function findById($mediaID){
        return $this->mapper->findById($mediaID);
    }
    public function delete($mediaID){
        return $this->mapper->deleteById($mediaID);
    }
