    //http://stackoverflow.com/a/15875555/1225672
    protected function guidv4()
    {
        $data = random_bytes(16);
        assert(strlen($data) == 16);

        $data[6] = chr(ord($data[6]) & 0x0f | 0x40); // set version to 0100
        $data[8] = chr(ord($data[8]) & 0x3f | 0x80); // set bits 6-7 to 10

        return vsprintf('%s%s-%s-%s-%s-%s%s%s', str_split(bin2hex($data), 4));
    }

    public function save(Media &$media){
        $ret = $this->mapper->save($media);

        return $ret;
    }
    public function saveOld(Media &$media){
        /* Make sure that Upload Directory is properly configured and writable */
        $path = $this->app['config']['ImagePath'];
        if(!file_exists($path)){
            if(!mkdir($path)){
                throw new ServiceException('Failed to create directory: '.$path);
            }
        }
        $ext = $this->mime2ext($media->type);
        if($ext===FALSE){
            throw new ServiceException('Unknown file type');
        }

        //if existing media
        if($media->mediaID!=NULL){
            //load existing
            $prev = $this->findById($media->mediaID);
            if($prev){
                $media->localfile = $prev->localfile;
            }

        }else{//
            //create localfile name
            $localfilename = $this->guidv4();
            //add file ext
            $media->localfile = $localfilename.'.'.$ext;
        }
        //if buffer is not null then save it to local storage
        if(!empty($media->buffer)){
            $filepath = $path.$media->localfile;
            $source = fopen($media->buffer, 'r');
            $destination = fopen($filepath, 'w+');

            stream_copy_to_stream($source, $destination);

            fclose($source);
            fclose($destination);

        }

        $ret = $this->mapper->save($media);
        if($ret==FALSE){
            unlink($filepath);
        }
        return $ret;
    }

    public function getContent(Media $media){
        $path = $this->app['config']['ImagePath'];
        $filepath = $path.$media->localfile;
        if(file_exists($filepath)){
            return file_get_contents($filepath);
        }
        throw new ServiceException('File not found: '.$filepath);
    }
}
